jQuery((function($){console.log("%ctooltip.js","color:#00ff00","-> Loaded");const tooltipList=document.querySelectorAll(".tooltip");tooltipList.forEach((el=>{const element=el.querySelector(".tooltip-element");const tooltip=el.querySelector(".tooltip-text");const popperInstance=Popper.createPopper(element,tooltip,{placement:"top",modifiers:[{name:"offset",options:{offset:[0,8]}}]});function show(){tooltip.setAttribute("data-show","");popperInstance.setOptions((options=>({...options,modifiers:[...options.modifiers,{name:"eventListeners",enabled:true}]})));popperInstance.update()}function hide(){tooltip.removeAttribute("data-show");popperInstance.setOptions((options=>({...options,modifiers:[...options.modifiers,{name:"eventListeners",enabled:false}]})))}const showEvents=["mouseenter","focus"];const hideEvents=["mouseleave","blur"];showEvents.forEach((event=>{element.addEventListener(event,show)}));hideEvents.forEach((event=>{element.addEventListener(event,hide)}))}))}));